import React,{Fragment, useEffect, useState} from 'react'
import { Link } from 'react-router-dom';
import Axios from 'axios';

let Home = () => {

     let [user, setUser] = useState([]);

     useEffect(() => {
         getAllUsers();
     }, []);

     let getAllUsers = () =>{
      Axios.get('/api/users').then((response)=>{
          setUser(response.data);
      }).catch((err)=>{
         console.log(err);
      });
     }
    return (
        <Fragment>
            <div className='container mt-3 '>
               <div className='row'>
                 <div className='col'>
                     <h1 className=' text-primary text-center'>User Details</h1>
                     <p className='lead'>The homepage or home page is the name of the main page of a website where visitors can find hyperlinks to other pages on the site. ... A home page may also be referred to as a "front page," "welcome page," or "landing page." Website homepage vs. browser homepage.</p>
                   <Link to='/add-user' className='btn btn-success btn-sm'>Add User</Link>
                 </div>
               </div>
            </div>

            <div className='container mt-3'>
               <div className='row'>
                   <div className='col'>
                       <table className='table table-hover table-striped text-center table-light'>
                           <thead className='bg-dark text-white'>
                               <tr>
                                   <td>SNO</td>
                                   <td>First Name</td>
                                   <td>Last Name</td>
                                   <td>EMAIL</td>
                                   <td>GENDER</td>
                                   <td>MOBILE NUMBER</td>
                                   <td>ACTIONS</td>
                               </tr>
                           </thead>
                       </table>

                   </div>

               </div>
            </div>
        </Fragment>
    )
}

export default Home;
